typeProperties.command:
.create-or-alter function with (folder = "Views", docstring = "View to get workloads for API Service.", skipvalidation = "true")
    View_APIServiceGetWorkloads(p_JobId:string) {
        /////////////////////////////////////////////////////
        //
        // View_APIServiceGetWorkloads(p_JobId='2517257745377723300_ed2f4050-dfcf-4265-b3df-e5ac96d9e8d9')
        /////////////////////////////////////////////////////
        //
        GetWorkloads_Cache
        | where RootJobId == p_JobId
        | order by WorkloadOrder asc, ServiceOrder asc, DeploymentPackageStartTime asc, ServiceTaskOrder asc
    }
