typeProperties.command:
.create-or-alter function with (folder = "Views", docstring = "View to get ESTS Root Jobs Filter Info for API Service.", skipvalidation = "true") 
    View_APIServiceGetEstsRootJobFilterInfo() {
        /////////////////////////////////////////////////////
        //
        //     View_APIServiceGetEstsRootJobFilterInfo()
        /////////////////////////////////////////////////////
        //
        CIS_Metadata_Job
        | where Workflow in ('ESTS_NewRegion')
        | project JobId, DisplayName, CreatedTime = CreatedDate, Region = parse_json(ParametersJson)['RegionName']
    }
