typeProperties.command:
.create-or-alter function with (folder = "Views", docstring = "View to get CIS Task Details by job for API Service.", skipvalidation = "true") 
    View_APIServiceGetCisTaskDetails(p_JobId: string) {
        /////////////////////////////////////////////////////
        //
        // View_APIServiceGetCisTaskDetails(p_JobId='2517682757198309999_a716ea70-42d8-44d6-89bb-7bf732d69a10')
        /////////////////////////////////////////////////////
        //
        let subJobIds = CIS_Metadata_Task
        | where JobId in (p_JobId) and ProgressReportLink != ''
        | project JobId = tostring(split(ProgressReportLink, "/")[-1]), SelfOrderId = OrderId;
        let leafJobIds = CIS_Metadata_Task
        | join kind = inner subJobIds on JobId
        | where ProgressReportLink != ''
        | project JobId = tostring(split(ProgressReportLink, "/")[-1]), ParentOrderId = SelfOrderId, SelfOrderId = OrderId;
        ((CIS_Metadata_Task
        | where JobId in (p_JobId)
        | project TaskId, TaskName, DisplayName, Component, Level1Order = toint(OrderId) - 1, Level2Order = -1, Level3Order = -1, Level = 1, OrderId = toint(OrderId), CreatedDate, StartDate, FinishDate, StateName)
        | union(CIS_Metadata_Task
        | join kind = inner subJobIds on JobId
        | project TaskId, TaskName, DisplayName, Component, Level1Order = toint(SelfOrderId) - 1, Level2Order = toint(OrderId) - 1, Level3Order = -1, Level = 2, OrderId = toint(OrderId), CreatedDate, StartDate, FinishDate, StateName)
        | union(CIS_Metadata_Task
        | join kind = inner leafJobIds on JobId
        | project TaskId, TaskName, DisplayName, Component, Level1Order = toint(ParentOrderId) - 1, Level2Order = toint(SelfOrderId) - 1, Level3Order = toint(OrderId) - 1, Level = 3, OrderId = toint(OrderId), CreatedDate, StartDate, FinishDate, StateName))
        | join kind = leftouter CIS_Metadata_Incident on TaskId
        | project TaskId, TaskName, DisplayName, Component, Level1Order, Level2Order, Level3Order, Level, OrderId, CreatedDate, StartDate, FinishDate, StateName, IcmLink = ExternalLink
        | order by Level1Order asc, Level2Order asc, Level3Order asc
    }
